import { useState, useEffect, createContext, useCallback } from "react";
import { Row, Col, Container } from "reactstrap";
import { read, utils, writeFileXLSX } from "xlsx";

import XLFile from "./data/data.xlsx";

const Filters = ({ handleCreateFilter, selectedFilters }) => {
	return (
		<>
			<p className="text-center mt-2 mb-1 fw-bold">Filter</p>
			<hr />
			<div className="">
				<label htmlFor="" className="fw-bold mb-1">
					Part name
				</label>
				<div className="checkbox">
					<input
						type="checkbox"
						name="PART_NAME_EN"
						id=""
						value="Jodia 1"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; Jodia 1
				</div>
			</div>
			<div className="mt-4">
				<label htmlFor="" className="fw-bold mb-1">
					PS Building Name
				</label>
				<div className="checkbox">
					<input
						type="checkbox"
						name="PSBUILDING_NAME_EN"
						id=""
						value="Branch School-1 Building, Para Area, Jodiya, Jodiya"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; Branch School-1 Building, Para Area, Jodiya, Jodiya
				</div>
			</div>
			<div className="mt-4">
				<label htmlFor="" className="fw-bold mb-1">
					Section Name
				</label>
				<div className="checkbox">
					<input
						type="checkbox"
						name="SECTION_NAME_EN"
						id=""
						value="BRAMAN STREET, JODIA-1"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; BRAMAN STREET, JODIA-1
				</div>
			</div>
			<div className="mt-4">
				<label htmlFor="" className="fw-bold mb-1">
					Firstname
				</label>
				<div className="checkbox">
					<input
						type="checkbox"
						name="FM_NAME_EN"
						id=""
						value="BHAGVATIBEN"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; BHAGVATIBEN
				</div>
				<div className="checkbox">
					<input
						type="checkbox"
						name="FM_NAME_EN"
						id=""
						value="DHIRUBHAI"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; DHIRUBHAI
				</div>
			</div>
			<div className="mt-4">
				<label htmlFor="" className="fw-bold mb-1">
					Lastname
				</label>
				<div className="checkbox">
					<input
						type="checkbox"
						name="LASTNAME_EN"
						id=""
						value="ZALA"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; ZALA
				</div>
				<div className="checkbox">
					<input
						type="checkbox"
						name="LASTNAME_EN"
						id=""
						value="JADEJA"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; JADEJA
				</div>
				<div className="checkbox">
					<input
						type="checkbox"
						name="LASTNAME_EN"
						id=""
						value="PARESHA"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; PARESHA
				</div>
				<div className="checkbox">
					<input
						type="checkbox"
						name="LASTNAME_EN"
						id=""
						value="BABARIYA"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; BABARIYA
				</div>
			</div>
			<div className="mt-4">
				<label htmlFor="" className="fw-bold mb-1">
					Gender
				</label>
				<div className="checkbox">
					<input
						type="checkbox"
						name="GENDER"
						id=""
						value="M"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; Male
				</div>
				<div className="checkbox">
					<input
						type="checkbox"
						name="GENDER"
						id=""
						value="F"
						onChange={(e) => handleCreateFilter(e)}
					/>
					&nbsp; Female
				</div>
			</div>
			<div className="mt-4">
				<label htmlFor="" className="fw-bold mb-1">
					Pincode
				</label>
				<div className="checkbox">
					<input type="checkbox" name="PIN_CODE" id="" value="361250" />
					&nbsp; 361250
				</div>
			</div>
		</>
	);
};

const UserRows = ({ user }) => {
	return (
		<tr>
			<td>{user.PART_NAME_EN}</td>
			<td>{user.PSBUILDING_NAME_EN}</td>
			<td>{user.SECTION_NAME_EN}</td>
			<td>{user.FM_NAME_EN}</td>
			<td>{user.LASTNAME_EN}</td>
			<td>{user.GENDER}</td>
			<td>{user.AGE}</td>
			<td>{user.PIN_CODE}</td>
		</tr>
	);
};

const UserTable = ({ users, selectedFilters, filters }) => {
	const rows = [];
	let newData = [];

	if (filters > 0) {
		const filterKeys = Object.keys(selectedFilters);
		// selectedFilters all elements passing the criteria
		let newUsers = users.filter((item) => {
			// dynamically validate all filter criteria
			return filterKeys.every((key) => {
				// ignores an empty filter
				if (!selectedFilters[key].length) return true;
				return selectedFilters[key].includes(item[key]);
			});
		});

		newUsers.forEach((user, index) => {
			rows.push(<UserRows user={user} key={index} />);
		});
		newData = newUsers;
	} else {
		users.forEach((user, index) => {
			rows.push(<UserRows user={user} key={index} />);
		});
	}

	const exportFile = useCallback(() => {
		const ws = utils.json_to_sheet(newData);
		const wb = utils.book_new();
		utils.book_append_sheet(wb, ws, "Data");
		writeFileXLSX(wb, "new-user.xlsx");
	}, [newData]);

	return (
		<>
			<div className="my-2">
				<p className="text-center my-2 fw-bold" style={{ float: "left" }}>
					77 - Jamnagar Rular
				</p>
				<button onClick={exportFile} style={{ float: "right" }}>
					Export in excel
				</button>
			</div>
			<div>
				<table className="table table-bordered">
					<thead>
						<tr>
							<th>Part Name</th>
							<th>PSBuilding Name</th>
							<th>Section Name</th>
							<th>Fisrt Name</th>
							<th>Last Name</th>
							<th>Gender</th>
							<th>Age</th>
							<th>Pincode</th>
						</tr>
					</thead>
					<tbody>{rows}</tbody>
				</table>
			</div>
		</>
	);
};

const UserFilterTable = ({ users, result, setResult }) => {
	const [selectedFilters, setSelectedFilters] = useState({
		PART_NAME_EN: [],
		PSBUILDING_NAME_EN: [],
		SECTION_NAME_EN: [],
		FM_NAME_EN: [],
		LASTNAME_EN: [],
		GENDER: [],
		PIN_CODE: [],
	});
	const [filters, setFilters] = useState(0);

	const handleCreateFilter = (e) => {
		const fieldName = e.target.name;
		const fieldValue = e.target.value;
		const isChecked = e.target.checked;

		if (isChecked) {
			setSelectedFilters({
				...selectedFilters,
				[fieldName]: [...selectedFilters[fieldName], fieldValue],
			});
			setFilters(filters + 1);
		} else {
			let newArray = selectedFilters[fieldName].filter(
				(item) => item !== fieldValue
			);
			setSelectedFilters({
				...selectedFilters,
				[fieldName]: newArray,
			});
			setFilters(filters - 1);
		}
	};

	return (
		<>
			<Container fluid>
				<Row>
					<Col md={2}>
						<Filters
							handleCreateFilter={handleCreateFilter}
							selectedFilters={selectedFilters}
						/>
					</Col>
					<Col md={10}>
						<UserTable
							users={users}
							selectedFilters={selectedFilters}
							filters={filters}
						/>
					</Col>
				</Row>
			</Container>
		</>
	);
};

const App = () => {
	const [result, setResult] = useState([]);

	const getData = async () => {
		const file = await (await fetch(XLFile)).arrayBuffer();
		const wb = read(file);
		const ws = wb.Sheets[wb.SheetNames[0]];
		const data = utils.sheet_to_json(ws);
		setResult(data);
	};

	useEffect(() => {
		getData();
	}, []);

	return (
		<UserFilterTable users={result} result={result} setResult={setResult} />
	);
};

export default App;
