import { useState, useEffect, useCallback } from "react";
import { read, utils, writeFileXLSX } from "xlsx";

import XLFile from "../../data.xlsx";

import { ALLOW_EXTENTIONS, MAKE_CALLS } from "../utils/constants";

const ExlReader = () => {
	const [result, setResult] = useState([]);

	const getData = async () => {
		const file = await fetch(XLFile).arrayBuffer();
		const wb = read(file);
		const ws = wb.Sheets[wb.SheetNames[0]];
		const data = utils.sheet_to_json(ws);
		setResult(data);
	};

	useEffect(() => {
		getData();
	}, []);

	return (
		<div>
			<div className="">
				<input
					type="file"
					name="file"
					id="file"
					accept={ALLOW_EXTENTIONS}
					onChange={handleChange}
				/>
				<input type="submit" value="Process Triggers" onClick={handleFile} />
			</div>
		</div>
	);
};

export default ExlReader;
