const UserTable = ({ users, selectedFilters, filters }) => {
	const rows = [];
	let newData = [];

	if (filters > 0) {
		const filterKeys = Object.keys(selectedFilters);
		// selectedFilters all elements passing the criteria
		let newUsers = users.filter((item) => {
			// dynamically validate all filter criteria
			return filterKeys.every((key) => {
				// ignores an empty filter
				if (!selectedFilters[key].length) return true;
				return selectedFilters[key].includes(item[key]);
			});
		});

		newUsers.forEach((user, index) => {
			rows.push(<UserRows user={user} key={index} />);
		});
		newData = newUsers;
	} else {
		users.forEach((user, index) => {
			rows.push(<UserRows user={user} key={index} />);
		});
	}

	const exportFile = useCallback(() => {
		const ws = utils.json_to_sheet(newData);
		const wb = utils.book_new();
		utils.book_append_sheet(wb, ws, "Data");
		writeFileXLSX(wb, "new-user.xlsx");
	}, [newData]);

	return (
		<>
			<div>
				<p className="text-center my-2 fw-bold" style={{ float: "left" }}>
					77 - Jamnagar Rular
				</p>
				<button onClick={exportFile} style={{ float: "right" }}>
					Export in excel
				</button>
			</div>
			<div>
				<table className="table table-bordered">
					<thead>
						<tr>
							<td>PART_NAME_EN</td>
							<td>PSBUILDING_NAME_EN</td>
							<td>SECTION_NAME_EN</td>
							<td>FM_NAME_EN</td>
							<td>LASTNAME_EN</td>
							<td>GENDER</td>
							<td>AGE</td>
							<td>PIN_CODE</td>
						</tr>
					</thead>
					<tbody>{rows}</tbody>
				</table>
			</div>
		</>
	);
};

const UserFilterTable = ({ users, result, setResult }) => {
	const [selectedFilters, setSelectedFilters] = useState({
		PART_NAME_EN: [],
		PSBUILDING_NAME_EN: [],
		SECTION_NAME_EN: [],
		FM_NAME_EN: [],
		LASTNAME_EN: [],
		GENDER: [],
		PIN_CODE: [],
	});
	const [filters, setFilters] = useState(0);

	const handleCreateFilter = (e) => {
		const fieldName = e.target.name;
		const fieldValue = e.target.value;
		const isChecked = e.target.checked;

		if (isChecked) {
			setSelectedFilters({
				...selectedFilters,
				[fieldName]: [...selectedFilters[fieldName], fieldValue],
			});
			setFilters(filters + 1);
		} else {
			let newArray = selectedFilters[fieldName].filter(
				(item) => item !== fieldValue
			);
			setSelectedFilters({
				...selectedFilters,
				[fieldName]: newArray,
			});
			setFilters(filters - 1);
		}
	};

	return (
		<>
			<Container fluid>
				<Row>
					<Col md={2}>
						<Filters
							handleCreateFilter={handleCreateFilter}
							selectedFilters={selectedFilters}
						/>
					</Col>
					<Col md={10}>
						<UserTable
							users={users}
							selectedFilters={selectedFilters}
							filters={filters}
						/>
					</Col>
				</Row>
			</Container>
		</>
	);
};

const App = () => {
	const [result, setResult] = useState([]);

	const getData = async () => {
		const file = await (await fetch(XLFile)).arrayBuffer();
		const wb = read(file);
		const ws = wb.Sheets[wb.SheetNames[0]];
		const data = utils.sheet_to_json(ws);
		setResult(data);
	};

	useEffect(() => {
		getData();
	}, []);

	return (
		<UserFilterTable users={result} result={result} setResult={setResult} />
	);
};

