import { useState, useEffect, createContext } from "react";
import { read, utils } from "xlsx";
import XLFile from "../data/data.xlsx";

export const FilterContext = createContext();

// import XLFile from "../data/data.xlsx";

const FilterProvider = ({ children }) => {
	const [result, setResult] = useState([]);
	const [filterList, setFilterList] = useState({
		PART_NAME_EN: [],
		PSBUILDING_NAME_EN: [],
		SECTION_NAME_EN: [],
		FM_NAME_EN: [],
		LASTNAME_EN: [],
		GENDER: [],
		AGE: { min: 0, max: 0 },
		PIN_CODE: [],
	});

	const [selectedFilter, setSelectedFilter] = useState({
		PART_NAME_EN: [],
		PSBUILDING_NAME_EN: [],
		SECTION_NAME_EN: [],
		FM_NAME_EN: [],
		LASTNAME_EN: [],
		GENDER: [],
		PIN_CODE: [],
	});

	const [isFiltered, setIsFiltered] = useState(0);

	const handleSetFilter = () => {
		return;
	};

	// const filterValue = () result.filter((item) =>
	// 	Object.entries(selectedFilter).every(([q, find]) => find.includes(item[q]))
	// );

	const applyFilter = (data, filters) => {
		const filterKeys = Object.keys(filters);
		// filters all elements passing the criteria
		return data.filter((item) => {
			// dynamically validate all filter criteria
			return filterKeys.every((key) => {
				// ignores an empty filter
				if (!filters[key].length) return true;
				return filters[key].includes(item[key]);
			});
		});
	};

	const handleCreateFilter = (e) => {
		let fieldName = e.target.name;
		let fieldValue = e.target.value;

		if (e.target.checked) {
			setSelectedFilter({
				...selectedFilter,
				[fieldName]: [...selectedFilter[fieldName], fieldValue],
			});
			setIsFiltered(isFiltered + 1);
		} else {
			let filterData = selectedFilter[fieldName].filter((item) => {
				return item !== fieldValue;
			});
			setSelectedFilter({
				...selectedFilter,
				[fieldName]: filterData,
			});
			setIsFiltered(isFiltered - 1);
		}
	};

	const getData = async () => {
		const file = await (await fetch(XLFile)).arrayBuffer();
		const wb = read(file);
		const ws = wb.Sheets[wb.SheetNames[0]];
		const data = utils.sheet_to_json(ws);
		setResult(data);
	};

	useEffect(() => {
		getData();
	}, []);

	return (
		<FilterContext.Provider
			value={{
				result,
				filterList,
				isFiltered,
				selectedFilter,
				handleSetFilter,
				handleCreateFilter,
			}}
		>
			{children}
		</FilterContext.Provider>
	);
};

export default FilterProvider;
