import { useState, useEffect, useCallback } from "react";
import { read, utils, writeFile } from "xlsx";

import { ALLOW_EXTENTIONS, MAKE_CALLS } from "../utils/constants";

const ExlReader = () => {
	const [file, setFile] = useState({});
	const [data, setData] = useState([]);
	const [cols, setCols] = useState([]);

	const handleFile = () => {
		const reader = new FileReader();
		const rABS = !!reader.readAsBinaryString;

		reader.onload = (e) => {
			/* Parse data */
			const bstr = e.target.result;
			const wb = XLSX.read(bstr, {
				type: rABS ? "binary" : "array",
				bookVBA: true,
			});
			/* Get first worksheet */
			const wsname = wb.SheetNames[0];
			const ws = wb.Sheets[wsname];
			/* Convert array of arrays */
			const newData = XLSX.utils.sheet_to_json(ws);
			/* Update state */
			setData(newData);
			setCols(MAKE_CALLS(ws["!ref"]));
			console.log(JSON.stringify(data, null, 2));
		};

		if (rABS) {
			reader.readAsBinaryString(file);
		} else {
			reader.readAsArrayBuffer(file);
		}
	};

	const handleChange = (e) => {
		const fileName = e.target.files;
		fileName && fileName[0] && setFile(fileName[0]);
	};

	return (
		<div>
			<div className="">
				<input
					type="file"
					name="file"
					id="file"
					accept={ALLOW_EXTENTIONS}
					onChange={handleChange}
				/>
				<input type="submit" value="Process Triggers" onClick={handleFile} />
			</div>
		</div>
	);
};

export default ExlReader;
